<div class="highlight"><pre style="background-color:#eed;-moz-tab-size:4;-o-tab-size:4;tab-size:4"><code class="language-go" data-lang="go"><span style="color:#8b008b;font-weight:bold">package</span> mut
<span style="color:#8b008b;font-weight:bold">import</span> (
<span style="color:#cd5555">&#34;sync&#34;</span>
)
<span style="color:#8b008b;font-weight:bold">type</span> Map[K, V comparable] <span style="color:#8b008b;font-weight:bold">struct</span> {
mu sync.RWMutex
values <span style="color:#8b008b;font-weight:bold">map</span>[K]V
}
<span style="color:#8b008b;font-weight:bold">func</span> (m *Map[K, V]) <span style="color:#008b45">Put</span>(key K, value V) {
m.mu.<span style="color:#008b45">Lock</span>()
m.values[key] = value
m.mu.<span style="color:#008b45">Unlock</span>()
}
<span style="color:#8b008b;font-weight:bold">func</span> (m *Map[K, V]) <span style="color:#008b45">Get</span>(key K) V {
<span style="color:#8b008b;font-weight:bold">var</span> v V
m.mu.<span style="color:#008b45">RLock</span>()
v = m.values[key]
m.mu.<span style="color:#008b45">RUnlock</span>()
<span style="color:#8b008b;font-weight:bold">return</span> v
}
<span style="color:#8b008b;font-weight:bold">func</span> NewMap[K, V comparable]() *Map[K, V] {
m := &amp;Map[K, V]{}
m.values = <span style="color:#658b00">make</span>(<span style="color:#8b008b;font-weight:bold">map</span>[K]V)
<span style="color:#8b008b;font-weight:bold">return</span> m
}
</code></pre></div>- https://blog.yearnfar.com/posts/go1.18%E6%B3%9B%E5%9E%8B%E5%AE%9E%E7%8E%B0%E5%B9%B6%E5%8F%91%E5%AE%89%E5%85%A8%E7%9A%84map/ - yearnfar 2022
